package com.sina.parkingApplication.model;import com.sina.parkingApplication.common.BaseEntity;import lombok.Data;import javax.persistence.*;import java.util.Date;@Entity@Data@Table(name = ParkingDetail.PARKING_DETAIL_TBL)public class ParkingDetail extends BaseEntity {    public static final String PARKING_DETAIL_TBL = "parking_detail_tbl";    @Temporal(TemporalType.TIMESTAMP)    private Date timeIn;    @Temporal(TemporalType.TIMESTAMP)    private Date timeOut;    private Long finalPrice;    @ManyToOne()    @JoinColumn(name = "parking_id", referencedColumnName = "id")    private Parking parking;    @ManyToOne()    @JoinColumn(name = "vehicle_id", referencedColumnName = "id")    private Vehicle vehicle;    @ManyToOne()    @JoinColumn(name = "price_rate_id", referencedColumnName = "id")    private PriceRate priceRate;}