package com.sina.parkingApplication.repository;import com.sina.parkingApplication.model.ParkingDetail;import org.springframework.data.domain.Page;import org.springframework.data.domain.Pageable;import org.springframework.data.jpa.domain.Specification;import org.springframework.data.jpa.repository.JpaSpecificationExecutor;import org.springframework.data.repository.PagingAndSortingRepository;import java.util.List;public interface ParkingDetailRepository extends PagingAndSortingRepository<ParkingDetail, Long>, JpaSpecificationExecutor<ParkingDetail> {    Page<ParkingDetail> findAllByVehicle_PlateNumber(String plateNumber, Pageable pageable);    List<ParkingDetail> findAll(Specification<ParkingDetail> parkingDetailSpecification);}