package com.sina.parkingApplication.service.impl;import com.sina.parkingApplication.common.SearchCriteria;import com.sina.parkingApplication.common.exception.NotFoundException;import com.sina.parkingApplication.model.Parking;import com.sina.parkingApplication.model.ParkingDetail;import com.sina.parkingApplication.model.PriceRate;import com.sina.parkingApplication.model.Vehicle;import com.sina.parkingApplication.repository.ParkingDetailRepository;import com.sina.parkingApplication.service.IParkingDetailService;import com.sina.parkingApplication.service.IParkingService;import com.sina.parkingApplication.service.IPriceRateService;import com.sina.parkingApplication.service.IVehicleService;import com.sina.parkingApplication.specification.ParkingDetailSpecification;import lombok.AllArgsConstructor;import lombok.extern.slf4j.Slf4j;import org.springframework.data.domain.Page;import org.springframework.data.domain.PageRequest;import org.springframework.data.domain.Sort;import org.springframework.stereotype.Service;import java.util.List;import java.util.Optional;@AllArgsConstructor@Service@Slf4jpublic class ParkingDetailService implements IParkingDetailService {    private final ParkingDetailRepository parkingDetailRepository;    private final IVehicleService vehicleService;    private final IParkingService parkingService;    private final IPriceRateService priceRateService;    @Override    public ParkingDetail registerRequest(ParkingDetail parkingDetail) {        Vehicle vehicle = vehicleService.getVehicle(parkingDetail.getVehicle().getId());        Parking parking = parkingService.getParking(parkingDetail.getParking().getId());        PriceRate priceRate = priceRateService.getPriceRate(parkingDetail.getPriceRate().getId());        parkingDetail.setVehicle(vehicle);        parkingDetail.setParking(parking);        parkingDetail.setPriceRate(priceRate);        return parkingDetailRepository.save(parkingDetail);    }    @Override    public void deleteParkingDetail(Long parkingDetailId) {        ParkingDetail parkingDetail = getParkingDetail(parkingDetailId);        parkingDetailRepository.delete(parkingDetail);    }    @Override    public ParkingDetail exitRequest(ParkingDetail parkingDetail) {        ParkingDetail savedBefore = getParkingDetail(parkingDetail.getId());        savedBefore.setTimeIn(parkingDetail.getTimeIn());        savedBefore.setTimeOut(parkingDetail.getTimeOut());        savedBefore.setFinalPrice(calculateFinalPrice(savedBefore));        return parkingDetailRepository.save(savedBefore);    }    @Override    public ParkingDetail getParkingDetail(Long parkingDetailId) {        Optional<ParkingDetail> optionalParkingDetail = parkingDetailRepository.findById(parkingDetailId);        if (optionalParkingDetail.isPresent()) {            return optionalParkingDetail.get();        }        throw new NotFoundException("parkDetail not found");    }    @Override    public Page<ParkingDetail> paging(Integer page, Integer size) {        return parkingDetailRepository.findAll(PageRequest.of(page, size, Sort.by("id").ascending()));    }    @Override    public Page<ParkingDetail> reportVehicleTraffic(String plateNumber, Integer page, Integer size) {        return parkingDetailRepository.findAllByVehicle_PlateNumber(plateNumber, PageRequest.of(page, size, Sort.by("id").ascending()));    }    @Override    public List<ParkingDetail> search(List<SearchCriteria> searchCriteria) {        ParkingDetailSpecification parkingDetailSpecification = new ParkingDetailSpecification();        searchCriteria.forEach(criteria -> parkingDetailSpecification.add(criteria));        return parkingDetailRepository.findAll(parkingDetailSpecification);    }    private Long calculateFinalPrice(ParkingDetail parkingDetail) {        long time_difference = parkingDetail.getTimeOut().getTime() - parkingDetail.getTimeIn().getTime();        long hours_difference = (time_difference / (1000 * 60 * 60)) % 24;        long days_difference = (time_difference / (1000 * 60 * 60 * 24));        log.info("days difference " + days_difference);        long month_difference = days_difference / 30;        days_difference %= 30;        log.info("hours " + hours_difference + "---daily " + days_difference + "---month " + month_difference);        Long answer = parkingDetail.getParking().getBasePrice();        answer += hours_difference * parkingDetail.getPriceRate().getHourlyRate();        if (days_difference == 0)            return answer;        answer += days_difference * parkingDetail.getPriceRate().getDailyRate();        if (month_difference == 0) {            return answer;        }        answer += month_difference * parkingDetail.getPriceRate().getMonthlyRate();        return answer;    }}